mapper.select:
SELECT re.id,re.name,re.parentId,re.resUrl
FROM t_resources re LEFT JOIN t_role_resources rr
ON re.id = rr.resourcesId
LEFT JOIN t_admin_role ur
ON rr.roleId =ur.roleId
WHERE ur.adminId=#{adminId}

    AND re.type= #{type}


   and re.parentId = #{parentId}

GROUP BY re.id
ORDER BY re.sort ASC
